import React from 'react';
import './Home.css';
import { UserOutlined, HeaderSearch, ConsoleSqlOutlined } from '@ant-design/icons';
import { Select, Avatar, Card, Input } from 'antd';
import { AudioOutlined } from '@ant-design/icons';
import { useState } from 'react';
import { useNavigate } from "react-router-dom";


const provinceData = ['ACCOUNT','SignIn', 'SignUp'];
const cityData = ['SignIn', 'SignUp']



function Home() {
    const navigate = useNavigate()

   const handleProvinceChange = (value) => {
        console.log(value)
        if(value == "SignIn"){
            navigate("/signin")
        }else if(value == "SignUp"){
            navigate("/signup")
        }
   };
  
  const { Search } = Input;
    const suffix = (
    <AudioOutlined
        style={{
        fontSize: 16,
        color: '#1890ff',
        }}
    />
    );
const onSearch = (value) =>{
    console.log(value);
} 
 
  return (
    <>
      <Card  hoverable 
     className="site-card-border-less-wrapper card">
       <div className='home-searchbar'>
        <div className='home-search'>
            <Search placeholder="input search text" enterButton="Search" size="large" suffix={suffix} onSearch={onSearch} />
        </div>
        <div className='home-avatar'>
            <Avatar size={40} icon={<UserOutlined />} />
            <Select
                className='homoe-select' 
                    style={{
                    width: 180,
                   }}
                    onChange={handleProvinceChange}
                    options={cityData.map((province) => ({
                        label: province,
                        value: province,
                    }))}
            />
      
        </div>
      </div>
      </Card>
    </>
  )
}

export default Home
