import React from 'react';
import './Home.css';
import { UserOutlined, HeaderSearch } from '@ant-design/icons';
import { Select, Avatar, Card, Input } from 'antd';
import { AudioOutlined } from '@ant-design/icons';
import { useState } from 'react';


const provinceData = ['ACCOUNT','SignIn', 'SignUp'];
const cityData = {
   SignUp: ['Account','SignIn', 'SignUp'],
};



function Home() {

  const [cities, setCities] = useState(cityData[provinceData[0]]);
  const [secondCity, setSecondCity] = useState(cityData[provinceData[0]]);
  const handleProvinceChange = (value) => {
    setCities(cityData[value]);
    setSecondCity(cityData[value][0]);
  };
  const onSecondCityChange = (value) => {
    setSecondCity(value);
  };

    const { Search } = Input;
const suffix = (
  <AudioOutlined
    style={{
      fontSize: 16,
      color: '#1890ff',
    }}
  />
);
const onSearch = (value) => console.log(value);
  return (
    <>
      <Card  hoverable 
     className="site-card-border-less-wrapper card">
       <div className='home-searchbar'>
        <div className='home-search'>
            <Search placeholder="input search text" enterButton="Search" size="large" suffix={suffix} onSearch={onSearch} />
        </div>
        <div className='home-avatar'>
            <Avatar size={40} icon={<UserOutlined />} />
            <Select
            className='homoe-select'
        defaultValue={provinceData[0]}
        style={{
          width: 180,
        }}
        onChange={handleProvinceChange}
        options={provinceData.map((province) => ({
          label: province,
          value: province,
        }))}
      />
      
        </div>
      </div>
      </Card>
    </>
  )
}

export default Home
