import React from 'react';
import './Home.css';
import { UserOutlined, HeaderSearch, ConsoleSqlOutlined } from '@ant-design/icons';
import { Select, Avatar, Card, Input } from 'antd';
import { AudioOutlined } from '@ant-design/icons';
import { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";



function Home() {
    const [searchBarValue, setSearchBarValue] = useState()
    const [searchbarList, setSearchBarList] = useState()
    const navigate = useNavigate()
    const provinceData = ['Account','SignIn', 'SignUp'];
    const cityData = ['SignIn', 'SignUp']
    const handleProvinceChange = (value) => {
        console.log(value)
      
      if(value == "SignIn"){
            navigate("/signin")
        }else if(value == "SignUp"){
            navigate("/signup")
        }
      };
  
    const { Search } = Input;
    const suffix = (
    <AudioOutlined
        style={{
        fontSize: 16,
        color: '#1890ff',
        }}
    />
    );

    const onSearch = (value) =>{
      const a= fetch(`https://v1.nocodeapi.com/atta/gr/IFCnaBwDjJwDcsef/search?q=${value}`);
      a.then((aaa)=>{
        console.log(aaa, 'a')
      })
    } 
 

// api call
    const getBooksLibraryData = () => {
      fetch("https://expertcrmapi-preprod.findanexpert.net/api/Service/SearchService",{
      method: "POST",
      headers: {"Content-type": "application/json;charset=UTF-8", authorization: "bearer XzEyMzQ1Ni5AUEkudjAwM3lwTyGD2uFY03Tzx46ofpw="  },
      body: JSON.stringify({search: searchbarvalue ,country:1})
      })
    .then(response =>  response
         
      ) 
    }  
  
 
  return (
    <>
      <Card  hoverable 
     className="site-card-border-less-wrapper card">
       <div className='home-searchbar'>
        <div className='home-search'>
            <Search placeholder="input search text" enterButton="Search" onChange={(e)=> setSearchBarValue(e.target.value)} value={searchBarValue} size="large" suffix={suffix} onSearch={getBooksLibraryData} />
          
            <ul>
              <li>{searchbarList}</li>
            </ul>
        </div>
        <div className='home-avatar'>
            <Avatar size={40} icon={<UserOutlined />} />
            <Select
                className='homoe-select' 
                    style={{
                    width: 180,
                   }}
                    onChange={handleProvinceChange}
                    options={cityData.map((province) => ({
                        label: province,
                        value: province,
                    }))}
            />
      
        </div>
      </div>
      </Card>
    </>
  )
}

export default Home
